import React, {Component, useRef} from 'react'
import { useReactToPrint } from "react-to-print";
import axios from 'axios'
import Main from '../template/Main'


const baseUrl = 'http://localhost:3001/livros'

const initialState = {
    livros:{nome: '', autor:''},
    list:[]
}

const baixar = () => {
    const componentRef = useRef();
    const handlePrint = useReactToPrint({
      content: () => componentRef.current,
    });
  
    return (
      <div>
        <Download ref={componentRef} />
        <button onClick={handlePrint}>Print this out!</button>
      </div>
    );
  };



const headerProps = {
    icon: 'fas fa-download',
    title: 'Download',
    subtitle: 'Aqui estÃ¡ a sua lista de livros! Mantenha seu acervo organizado!'
}

export default class Download extends Component{
    state = {...initialState}


    componentWillMount(){
        axios(baseUrl).then(resp =>{
            this.setState({list: resp.data})
        })
    }
    clear(){
        this.setState({livros: initialState.livros})
    }

    save(){
        const livros = this.state.livros
        const method = livros.id ? 'put' : 'post'
        const url = livros.id ? `${baseUrl}/${livros.id}` : baseUrl
        axios[method](url, livros)
        .then(resp =>{
            const list = this.getUpdateList(resp.data)
            this.setState({livros: initialState.livros, list})
        })
    }

    
    getUpdateList(livros, add = true){
        const list = this.state.list.filter(u => u.id !== livros.id)
        if(add) list.unshift(livros)
        return list
    }

    updateField(event){
        const livros = {...this.state.livros}
        livros[event.target.name] = event.target.value
        this.setState({livros})
    }


    renderForm(){
        return(
            <div className="form">
                <div className="row">
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label>Nome</label>
                            <input type="text" className="form-control"
                                name="nome"
                                value={this.state.livros.nome}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o nome do livro..."/>

                        </div>

                    </div>
                    <div className="col-12 col-md-6">
                        <div className="form-group">
                            <label>Autor</label>
                            <input type="text" className="form-control"
                                name="autor"
                                value={this.state.livros.autor}
                                onChange={e => this.updateField(e)}
                                placeholder="Digite o nome do autor..."/>

                        </div>

                    </div>
                     
                </div>
                <hr />
                    <div className="row">
                        <div className="col-12 d-flex justify-content-end">
                            <button className="btn btn-primary"
                                onClick={e => this.save(e)}>
                                Salvar
                            </button>

                            <button className="btn btn-secondary ml-2"
                                onClick={e => this.clear(e)}>
                                Cancelar
                            </button>

                        </div>
                    
                    </div>

            </div>
        )
    }

    load(livros){
        this.setState({livros})
    }

    remove(livros){
        axios.delete(`${baseUrl}/${livros.id}`).then(
            resp => {
               const list = this.getUpdateList(livros,false)
               this.setState({list}) 
            }
        )
    }

    renderRows(){
        return this.state.list.map(livros => {
            return(
                <tr key={livros.id}>
                    <td>{livros.id}</td>
                    <td>{livros.nome}</td>
                    <td>{livros.autor}</td>
                </tr>
            )
        })
    }

    renderTable(){
        return(
            <table className="table mt-4">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Autor</th>
                    </tr>
                </thead>
                <tbody>{this.renderRows()}</tbody>

            </table>
        )
    }

    render(){
        return(
            
            <Main {...headerProps}>
                <button className="btn btn-primary">Baixar</button>
                {this.renderTable()}
            </Main>
            
        )
    }

}
